import { Component } from '@angular/core';

@Component({
  selector: 'app-anmeldung',
  templateUrl: './anmeldung.component.html',
  styleUrls: ['./anmeldung.component.scss']
})
export class AnmeldungComponent {
   toggleText():void {
    const checkbox = document.getElementById('toggle-switch') as HTMLInputElement;;
    const slider = document.querySelector('.slider:before') as HTMLElement;;
    if (checkbox.checked) {
      slider.style.content = 'Recruiter';
    } else {
      slider.style.content = 'Bewerber';
    }
  }
}
<header class="fixed-header">
    <nav>
      <ul>
        <li><a routerLink="/">Start</a></li>
      </ul>
    </nav>
  </header>

  <section>
    <h2>Login</h2>
    <label class="switch">
        <input type="checkbox" id="toggle-switch" (click)="toggleText()">
        <span class="slider"></span>
      </label>

      <script>
      function toggleText() {
        var checkbox = document.getElementById('toggle-switch');
        var slider = document.querySelector('.slider:before');
        if (checkbox.checked) {
          slider.style.content = 'Recruiter';
        } else {
          slider.style.content = 'Bewerber';
        }
      }
      </script>

  </section>

  <footer>
    <p>&copy; 2024 Unternehmensname. Alle Rechte vorbehalten.</p>
  </footer>
