import { Component } from '@angular/core';

@Component({
  selector: 'app-anmeldung',
  templateUrl: './anmeldung.component.html',
  styleUrls: ['./anmeldung.component.scss']
})
export class AnmeldungComponent {
  isRecruiter = true;

  toggleRole(): void {
    this.isRecruiter = !this.isRecruiter;
  }
}
<header class="fixed-header">
    <nav>
      <ul>
        <li><a routerLink="/">Start</a></li>
      </ul>
    </nav>
  </header>

  <section>
    <h2>Login</h2>
    <div class="toggle-container">
        <input type="checkbox" id="toggle-button" (change)="toggleRole()" [checked]="isRecruiter">
        <label for="toggle-button" class="toggle-label">
          <span class="toggle-slider">
            <span class="slider-text">{{ isRecruiter ? 'Recruiter' : 'Bewerber' }}</span>
          </span>
        </label>
      </div>

  </section>

  <footer>
    <p>&copy; 2024 Unternehmensname. Alle Rechte vorbehalten.</p>
  </footer>
