import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms'; //Für 



@Component({
  selector: 'app-anmeldung',
  templateUrl: './anmeldung.component.html',
  styleUrls: ['./anmeldung.component.scss']
})
export class AnmeldungComponent {
  isRecruiter = false;

  toggleRole(): void {
    this.isRecruiter = !this.isRecruiter;
  }

  loginForm = new FormGroup({
    email: new FormControl('', [Validators.required, Validators.email]),
    password: new FormControl('', Validators.required)
  });

  onSubmit(): void {
    // Hier würdest du mit deinem Authentifizierungsservice interagieren
    console.log(this.loginForm.value);
  }
}
