import { Component } from '@angular/core';

@Component({
  selector: 'app-anmeldung',
  templateUrl: './anmeldung.component.html',
  styleUrls: ['./anmeldung.component.scss']
})
export class AnmeldungComponent {
  isRecruiter = true; // Startzustand des Toggles

  get toggleLabel(): string {
    return this.isRecruiter ? 'Recruiter' : 'Bewerber';
  }

  toggleRole(): void {
    this.isRecruiter = !this.isRecruiter;
  }
}
<header class="fixed-header">
    <nav>
      <ul>
        <li><a routerLink="/">Start</a></li>
      </ul>
    </nav>
  </header>

  <section>
    <h2>Login</h2>
    <div class="toggle-container">
        <label class="switch">
          <input type="checkbox" (change)="onToggleChange()">
          <span class="slider round"></span>
        </label>
        <span>{{ toggleText }}</span> <!-- Text wird von der Komponente bezogen -->
      </div>

  </section>

  <footer>
    <p>&copy; 2024 Unternehmensname. Alle Rechte vorbehalten.</p>
  </footer>
